    /* ------------- `users` table method ------------------ */

    /**
     * Creating new user
     * @param String $email User login email id
     * @param String $password User login password
     */
    public function createUser($username, $email, $password) {
        require_once 'PassHash.php';
        $response = array();

        // First check if user already existed in db
        if (!$this->isUserExists($username, $email)) {
            // Generating password hash
            $password_hash = PassHash::hash($password);

            // insert query
            $stmt = $this->conn->prepare("INSERT INTO users(username, email, password_hash) values(?, ?, ?)");
			$stmt->bind_param("sss", $username, $email, $password_hash);

            $result = $stmt->execute();

            $stmt->close();
			if ($result) {
				$user = $this->getUserByEmail($email);
				$result = $this->updateProfilePic($user['id'], 'default');
				// Check for successful insertion
				if ($result == PIC_UPDATED_SUCCESSFULLY) {
				//if ($result) {
					// User successfully inserted
					return USER_CREATED_SUCCESSFULLY;
				} else {
					// Failed to create user
					return USER_CREATE_FAILED;
				}
			}
			else {
				return USER_CREATE_FAILED;
			}			
            
        } else {
            // User with same email already existed in the db
            return USER_ALREADY_EXISTS;
        }

        return $response;
    }

	public function updateProfilePic($user_id, $profilePic) {
		if ($profilePic == 'default') {
			//$profilePic = '';
			$profilePic = '';
			//$profilePic = '';
		}
		$stmt = $this->conn->prepare("UPDATE users set profilepic = ? where id = ?");
		$stmt->bind_param("si", $profilePic, $user_id);
		$res = $stmt->execute();
		$stmt->close();
		if ($res) {
			return PIC_UPDATED_SUCCESSFULLY;
		}else {
			return PIC_UPDATE_FAILED;
		}
	}

	/**
     * Updating an existing user's password
     * @param String $email User login email id
     * @param String $newEmail User login new email id
     */
	public function updatePassword($user_id, $oldPassword, $newPassword) {
		require_once 'PassHash.php';
        //$response = array();
		$user = $this->getUserById($user_id);
		if ($this->checkLogin($user['email'], $oldPassword)) {
			$password_hash = PassHash::hash($newPassword);

			$stmt = $this->conn->prepare("UPDATE users SET password_hash = ? WHERE id = ?");
            $stmt->bind_param("si", $password_hash, $user_id);

            $result = $stmt->execute();

            $stmt->close();

            // Check for successful insertion
            if ($result) {
				return PASSWORD_UPDATED_SUCCESSFULLY;
            } else {
				return PASSWORD_UPDATE_FAILED;
            }
		}
		else
		{
			return PASSWORD_UPDATE_INVALID_CREDENTIALS;
		}
	}

	/**
     * Updating an existing user's email
     * @param String $email User login email id
     * @param String $newEmail User login new email id
     */
	public function updateEmail($user_id, $newEmail) {
		// First check if user already existed in db
        if (!$this->isEmailExists($newEmail)) {

            $stmt = $this->conn->prepare("UPDATE users SET email = ? WHERE id = ?");
            $stmt->bind_param("si", $newEmail, $user_id);

            $result = $stmt->execute();

            $stmt->close();

            // Check for successful insertion
            if ($result) {
                // User successfully inserted
                return EMAIL_UPDATED_SUCCESSFULLY;
            } else {
                // Failed to create user
                return EMAIL_UPDATED_FAILED;
            }
        } else {
            // User with same email already existed in the db
            return USER_ALREADY_EXISTS;
        }
	}

    /**
     * Checking user login
     * @param String $email User login email id
     * @param String $password User login password
     * @return boolean User login status success/fail
     */
    public function checkLogin($email, $password) {
        // fetching user by email
        $stmt = $this->conn->prepare("SELECT password_hash FROM users WHERE email = ?");

        $stmt->bind_param("s", $email);

        $stmt->execute();

        $stmt->bind_result($password_hash);

        $stmt->store_result();

        if ($stmt->num_rows > 0) {
            // Found user with the email
            // Now verify the password

            $stmt->fetch();

            $stmt->close();

            if (PassHash::check_password($password_hash, $password)) {
                // User password is correct
                return TRUE;
            } else {
                // user password is incorrect
                return FALSE;
            }
        } else {
            $stmt->close();

            // user not existed with the email
            return FALSE;
        }
    }

    /**
     * Checking for duplicate user by email address
     * @param String $email email to check in db
     * @return boolean
     */
    private function isEmailExists($email) {
        $stmt = $this->conn->prepare("SELECT id from users WHERE email = ?");
        $stmt->bind_param("s", $email);
        $stmt->execute();
        $stmt->store_result();
        $num_rows = $stmt->num_rows;
        $stmt->close();
        return $num_rows > 0;
    }

    /**
     * Checking for duplicate user by email address
     * @param String $email email to check in db
     * @return boolean
     */
    private function isUserExists($username, $email) {
        $stmt = $this->conn->prepare("SELECT id from users WHERE username = ? or email = ?");
        $stmt->bind_param("ss", $username, $email);
        $stmt->execute();
        $stmt->store_result();
        $num_rows = $stmt->num_rows;
        $stmt->close();
        return $num_rows > 0;
    }

    /**
     * Fetching user by email
     * @param String $email User email id
     */
    public function getUserByEmail($email) {
        $stmt = $this->conn->prepare("SELECT id, username, profilepic FROM users WHERE email = ?");
        $stmt->bind_param("s", $email);
        if ($stmt->execute()) {
            $stmt->bind_result($id, $username,$profilepic);
            $stmt->fetch();
            $user = array();
            $user["username"] = $username;
            $user["id"] = $id;
            $user["email"] = $email;
            $user["profilepic"] = $profilepic;
            $stmt->close();
            return $user;
        } else {
            return NULL;
        }
    }
	
	public function getFavourites($user_id) {
		$stmt = $this->conn->prepare("select id, spotId from favourite where userId = ? ORDER BY spotId");
		$stmt->bind_param("i", $user_id);
		$stmt->execute();
		//$result=  = $stmt->get_result();	
		$stmt->store_result();
		$stmt->bind_result($id, $spotId);
		$spots = array();
		
		while ($stmt->fetch()) {
			$tmp = array();
			$tmp["id"] = $id;
			$tmp["spotId"] = $spotId;
			array_push($spots, $tmp);
		}
		$stmt->close();
		return $spots;
	}
	
	public function toggleFavourite($user_id, $spot_id) {		
		$stmt = $this->conn->prepare("SELECT id from favourite WHERE userId = ? and spotId = ?");
        $stmt->bind_param("ii", $user_id, $spot_id);
        $stmt->execute();
        $stmt->store_result();
        $num_rows = $stmt->num_rows;
        $stmt->close();
        if ($num_rows > 0) {
			$stmt = $this->conn->prepare("DELETE FROM favourite WHERE userId = ? and spotId = ?");
			$stmt->bind_param("ii", $user_id, $spot_id);
			$stmt->execute();
		}
		else
		{
			$stmt = $this->conn->prepare("INSERT INTO favourite (userId, spotId) VALUES(?, ?)");
			$stmt->bind_param("ii", $user_id, $spot_id);
			$stmt->execute();
		}
		
		return $this->getFavourites($user_id);
		
	}

	public function reportIssue($user_id, $spotId, $airdata, $seadata, $cam) {
