        $stmt->bind_param("isss", $spotId, $airdata, $seadata, $cam);
        $stmt->execute();
        $stmt->store_result();
        $num_rows = $stmt->num_rows;
        $stmt->close();

        if ($num_rows <= 0) {
			$stmt = $this->conn->prepare("INSERT INTO issues(userId, spotId, airdata, seadata, cam) values(?, ?, ?, ?, ?)");
            $stmt->bind_param("iisss", $user_id, $spotId, $airdata, $seadata, $cam);

            $result = $stmt->execute();

            $stmt->close();

            // Check for successful insertion
            if ($result) {
