	}


    public function getUserById($user_id) {
        $stmt = $this->conn->prepare("SELECT email, username, profilepic FROM users WHERE id = ?");
        $stmt->bind_param("i", $user_id);
        if ($stmt->execute()) {
            $stmt->bind_result($email, $username, $profilepic);
            $stmt->fetch();
            $user = array();
            $user["username"] = $username;
            $user["id"] = $user_id;
            $user["email"] = $email;
            $user["profilepic"] = $profilepic;
            $stmt->close();
            return $user;
        } else {
            return NULL;
        }
    }

