	/* ------------- `spot` table methods ------------------ */
	public function getAllSpots() {
		$stmt = $this->conn->prepare("SELECT s.id, s.name, s.coord, s.webcamURL, s.regionId, r.name as regionName FROM spot s INNER JOIN region r on s.regionId = r.id ORDER BY s.name");
            $stmt->execute();
            //$result=  = $stmt->get_result();	
			$stmt->bind_result($id, $name, $coord, $webcamURL, $regionId, $regionName);
			$spots = array();
			
			while ($stmt->fetch()) {
				$res = array();
				$res["id"] = $id;
				$res["name"] = $name;
				$res["coord"] = $coord;
				$res["webcamURL"] = $webcamURL;
				$res["regionId"] = $regionId;
				$res["regionName"] = $regionName;
				array_push($spots, $res);
			}
            $stmt->close();
            return $spots;
	}
}

